namespace RatEye
{
	public partial class Config
	{
		public partial class Processing
		{
			/// <summary>
			/// The Icon class contains parameters, used by the icon processing module.
			/// </summary>
			public class Icon
			{
				/// <summary>
				/// Use the static icons for template matching
				/// </summary>
				public bool UseStaticIcons = false;

				/// <summary>
				/// Use the icon cache, generated by the game, for template matching
				/// </summary>
				public bool UseDynamicIcons = false;

				/// <summary>
				/// If <see langword="true"/> all dynamic icons will be reloaded when
				/// the file at <see cref="Path.DynamicCorrelationData"/> is being modified
				/// </summary>
				public bool WatchDynamicIcons = true;

				/// <summary>
				/// If <see langword="true"/> the index file will be treated as being
				/// created by a version prior to 0.12.11.2.13615
				/// </summary>
				public bool UseLegacyCacheIndex = false;

				/// <summary>
				/// Scan for 90° rotated icons
				/// </summary>
				public bool ScanRotatedIcons = true;

				/// <summary>
				/// Create a new icon config instance
				/// </summary>
				public Icon() { }
			}
		}
	}
}
